import React, { Component } from 'react';
import InputField from './InputField';

export default class InnerBox extends React.Component{

    constructor(state) {
        super(state);
        this.state = {
          initialHeight:0,
          initialWeight:0,
          height:0,
          weight:0,
          bmi:0.0,
          weightRating:'',

        };

        this.changeHeight = this.changeHeight.bind(this);
        this.changeWeight = this.changeWeight.bind(this);
        this.calculate = this.calculate.bind(this);
        this.reset = this.reset.bind(this);

      }

    changeHeight(event){

        this.setState(
            {
                height:event.target.value
            }
        );
        // console.log(this.state.height);
    }  

    changeWeight(event){

        this.setState(
            {
               weight:event.target.value 
            }

        );
        // console.log(this.state.weight);
    }  

   calculate(){
        var height = this.state.height;
        var weight = this.state.weight;
        console.log('weight:', weight);
        console.log('height:', height);
        var bmi = (parseInt(weight) / parseInt(height) )/parseInt(height);
        var weightRating = this.state.weightRating;
        if(bmi <= 18.5){
                weightRating = "Underweight";
            }
            else if(bmi > 18.5 && bmi <=24.9){
                weightRating = "Normal Weight";
            }
            else if(bmi > 24.9 && bmi <=29){
                weightRating = "Overweight";
            }
            else{
                weightRating = "You are a thicc boi";
            }
        this.setState(
            { 
                bmi:bmi,
                weightRating:weightRating,
            }
        )
   }
   //get the value of the height, get the value of the weight
   //do the math
   //store the result in the bmi variable
   //set the states bmi to equal the bmi variable
   //display the states bmi in the header part
   //check the bmi variable, and depending on what it is, show the correct weight rating 

    reset(){
        this.setState(
            {
                initialHeight:0,
                initialWeight:0,
                height:0,
                weight:0,
                bmi:0.0,
                weightRating:'',
            }
        )
    }

    render(){
    //    console.log(this.state.height);
    //    console.log(this.state.weight);
        return(
            <div className="innerBox">
                <h2>Body Mass Index (BMI) = {this.state.bmi}</h2>

                <h3>{this.state.weightRating}</h3>

                <div className="InputWrap">

                    <div className="inputFieldWrap">
                        <label>Height (meters)</label>
                        <input placeholder="Enter your height"  onChange={this.changeHeight} value={this.state.height}/>
                    </div>
                    <div className="inputFieldWrap">
                        <label>Weight (kg.)</label>
                        <input placeholder="Enter your weight"  onChange={this.changeWeight} value={this.state.weight}/>
                    </div>

                </div>
                <button onClick={this.calculate}>Calculate BMI</button>
                <button onClick={this.reset}>RESET</button>
            </div>
        )
    }
}
