import React, { Component } from 'react';
import './App.css';

function Hero(){
  return(
    <div className="row">
      <div className="jumbotron col-10 offset-1">
        <h1>Author Quiz</h1>
        <p>Select the book written by the author shown.</p>
      </div>
    </div>
  )
}

function Continue(){
  return(
    <button className='continueBtn'>Continue</button>
  )
}

function Book({title}){
    return(
      <div className="answer">
        <h4>{title}</h4>
      </div>

    )
}

function Turn({author, books}){
    return(
      <div className="row turn" style={{backgroundColor: "#fff"}}>
        <div className="col-4 offset-1">
          <img src={author.imgUrl} className="authorImage"/>
        </div>
        <div className="col-6">
          {books.map((title) => <Book title={title} key={title} />)}
        </div>
      </div>
    )

}

function Footer(){
  return(
    <div className="row">

      <footer className="col-xs-12">This is the footer text</footer>
    </div>

  )
}

function AuthorQuiz({turnData}) {console.log(turnData);
  return(
      <div className='container-fluid'>
            <Hero/>
            <Turn {...turnData}/>
            <Continue/>
            <Footer/>
      </div>
  )



}
export default AuthorQuiz;
