import React, { Component } from 'react';
import InputField from './InputField';

export default class InnerBox extends React.Component{

    constructor(state) {
        super(state);
        this.state = {
          initialHeight:0,
          initialWeight:0,
          height:0,
          weight:0,
          bmi:0.0,
          weightRating:'',

        };

        this.changeHeight = this.changeHeight.bind(this);
        this.changeWeight = this.changeHeight.bind(this);
        this.calculator = this.calculator.bind(this);
        this.reset = this.reset.bind(this);

      }

    changeHeight(event){
       console.log(this.state.height);
        this.setState(
            {
                height:event.target.value
            }
        );
    }  

    changeWeight(event){
        console.log(this.state.weight);
        this.setState(
            {
               weight:event.target.value 
            }

        );
    }  

   calculator(){
        // const {weight} = this.state.weight;
        // const {height} = this.state.height;
        const bmi = 20;
        console.log(bmi);
        this.setState(
            { 
                bmi:bmi
            }
        )
   }









    reset(){
        this.setState(
            {
                initialHeight:0,
                initialWeight:0,
                height:0,
                weight:0,
                bmi:0.0,
                weightRating:'',
            }
        )
    }

    render(){
        // console.log('bmi:',bmi);
        // var bmi = this.state.bmi;
        // var weightRating = this.state.weightRating;
        // console.log(weightRating);
        // if(bmi <= 18.5){
        //     weightRating = "Underweight";
        // }
        // else if(bmi > 18.5 && bmi <=24.9){
        //     weightRating = "Normal Weight";
        // }
        // else if(bmi > 24.9 && bmi <=25){
        //     weightRating = "Overweight";
        // }
        // else{
        //     weightRating = "You are a thicc boi";
        // }

        return(
            <div className="innerBox">
                <h2>Body Mass Index (BMI) = {this.state.bmi}</h2>

                <h3>{this.state.weightRating}</h3>

                <div className="InputWrap">

                    <div className="inputFieldWrap">
                        <label>Height (meters)</label>
                        <input placeholder="Enter your height" value={this.state.height} onChange={this.changeHeight}/>
                    </div>
                    <div className="inputFieldWrap">
                        <label>Weight (kg.)</label>
                        <input placeholder="Enter your weight" value={this.state.weight} onChange={this.calculator}/>
                    </div>

                </div>
                <button onClick={this.reset}>RESET</button>
            </div>
        )
    }
}
