import React, { Component } from 'react';

export default class Input extends React.Component{

    searchName(event){
        console.log('searchName');
        this.setState({
            names: this.names.push(event.target.value)
        })
    }

    render(){
        return(
            <div className="inputWrap">
                <input placeholder="Add or search a name" ref="nameInput" onChange={this.searchName}></input>
            </div>
        )
    }
}
