import React, { Component } from 'react';

export default class NameWindow extends React.Component{
    // constructor(props){
    //     super(props);
    //     this.state = {
    //         names:['will','john','bob','rick']
    //     }



    // }

    addName(name){
        this.setState({
            names: names.push(name)
        })
    }


    render(){
        return(
            <div className="nameWindow">
                {this.state.names.map(function(name){
                    return <button className="nameInfoBtn" >{name}</button>
                })}
                <div className="btn-wrap">
                    <button className="addNameBtn" onClick={this.addName}>Add Name</button>
                </div>
            </div>
        )
    }
}
