import React, { Component } from 'react';
import './index.css';
import AddNameBtn from './AddNameBtn';
import InnerBox from './InnerBox';
import Input from './Input';
import NameWindow from './NameWindow';

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {

        search:'',
        newName:null,
    }

    this.search = this.search.bind(this);
    this.addName = this.addName.bind(this);

    this.inputRef = React.createRef();
    const names = ['will','bob','jim','james','rick','linda','johnny'];
    }



    search(e){
        this.setState(
            {
               search: e.target.value
            }

        );


        // console.log(this.state.search);
    }

    addName(){

        this.setState({
            names:[this.state.names, 'newname']
        })
    }

    render() {
        const {search} = this.state;
        const {names} = this.state;

        const filteredNames = names.filter(function(name){
            return name.toLowerCase().indexOf(search.toLowerCase()) !== -1
        })



        return(
        //calculator
            <div className="masterWrapper">
                <div className="wrapper">
                    <h1>BMI Calculator</h1>
                    <InnerBox/>
                </div>


                <div className="nameWrap">

                    <h4>Name Inspiration</h4>

                    <div className="inputWrap">
                        <input placeholder="Add or search a name" ref="nameInput"  onChange={this.search}></input>
                    </div>
                    <div className="nameWindow" names={this.state.names}>
                        {
                           filteredNames.map(name => {
                            return <button className="nameInfoBtn">{name}</button>
                           })
                        }
                        <form onSubmit={this.addName}>
                            <input placeholder="Add name"  className="wow"  value={this.state.newName}></input>
                            <button className="addNameBtn" onClick={this.addName}>Add name</button>
                        </form>

                    </div>

                </div>

            </div>

        )
    }



}
