import React, { Component } from 'react';
import './index.css';
import CalcBtn from './CalcBtn';
import InnerBox from './InnerBox';
import Input from './Input';
import NameWindow from './NameWindow';

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
        names:['will','john','bob','rick']
    }
    this.addName = this.addName.bind(this);


}

  addName(name){
        this.setState({
            names: this.state.names.push(name)
        })
    }

  render() {
    // console.log(this.state);
    return(
      //calculator
      <div className="masterWrapper">
          <div className="wrapper">
            <h1>BMI Healthy Weight Calculator</h1>
            <InnerBox/>

          </div>


          <div className="nameWrap">

            <h1>Baby Name Inspiration</h1>

            <div className="inputWrap">
              <input placeholder="Enter a letter"></input>
            </div>

            <div className="nameWindow">
                {this.props.names.map(function(name){
                    return <button className="nameInfoBtn" >{name}</button>
                })}
            </div>

            <div className="btn-wrap">
                <input onChange={this.addName}></input>
                <button className="addNameBtn" onClick={this.addName}>Add Name</button>
            </div>
          </div>
      </div>

    )
}



}
