import { render } from "react-dom";
import React from "react";

import AuthorQuiz from "./AuthorQuiz.js";

import "./bootstrap.min.css";

const authors = [
      {
            name:'Stephen King',
            imageUrl: 'https://images.gr-assets.com/authors/1362814142p8/3389.jpg',
            books:['The Shining', 'some other book', 'another book']
      },
      {
            name:'George R. R. Martin',
            imageUrl: '/img/george_martin.jpg',
            books:['A Game of Thrones','some other book', 'another book']
      },
      {
            name:'Mary Shelley',
            imageUrl: '/img/mary_shelley.jpg',
            books:['Frankenstein','some other book', 'another book']
      },
      {
            name:'Meditations',
            imageUrl: 'marcus_aurelius.jpg',
            books:['Marcus Aurelius','some other book', 'another book']
      },
]

function getTurnData(authors){
      const allBooks = authors.reduce(function(p,c,i){
            return p.concat(c.books);
      }, []);
      const fourRandomBooks = shuffle(allBooks).slice(0,4);
      const answer = sample(fourRandomBooks);
}

const state = {
      turnData:{
            author: authors[0],
            books: authors[0].books
      }
}

render(<AuthorQuiz {...state} />, document.querySelector("#root"));
