import React, { Component } from 'react';
import './index.css';
import AddNameBtn from './AddNameBtn';
import InnerBox from './InnerBox';
import Input from './Input';
import NameWindow from './NameWindow';

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
        names:['will','john','bob','rick'],

    }
    this.addName = this.addName.bind(this);
    this.searchName = this.searchName.bind(this);

    }

    addName(){
        console.log(value);
        const input = this.refs.nameInput;
        const value = input.value;
        console.log(value);
        this.setState({
            names: this.state.names.push(value)
        })
    }

    searchName(event){
        console.log('yo');
        this.setState({
            names: this.names.push(event.target.value)
        })
    }

    render() {
        // console.log(this.state);
        return(
        //calculator
            <div className="masterWrapper">
                <div className="wrapper">
                    <h1>BMI Healthy Weight Calculator</h1>
                    <InnerBox/>
                </div>


                <div className="nameWrap">

                    <h1>Baby Name Inspiration</h1>

                    <Input 
                        name={this.state.names}
                        onChange={this.searchName}
                    />

                    <NameWindow 
                        names={this.state.names}
                    />

                    <AddNameBtn 
                        onClick={this.addName}
                    />


                </div>

            </div>

        )
    }



}
