import React, { Component } from 'react';
import './index.css';
import CalcBtn from './CalcBtn';
import InnerBox from './InnerBox';

export default class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      initialHeight:0,
      initialWeight:0,
      height:'6 feet',
      weight:'200 pounds',
      bmi:0.0,
      weightRating:'',

    };
  }






  render() {
    // console.log(this.state);
    return(
      <div className="wrapper">
        <h1>BMI Healthy Weight Calculator</h1>
        <InnerBox>
          <InputField 
              label="Height (in.)"
              placeholder={this.props.initialHeight}  
              value={this.props.height}
          >

          </InputField>
          <InputField 
              label="Weight (ibs.)"
              placeholder={this.props.initialWeight}
              value={this.props.weight}
          >

          </InputField>
        </InnerBox>
      </div>

    )
}



}
