import React, { Component } from 'react';
import './index.css';
import AddNameBtn from './AddNameBtn';
import InnerBox from './InnerBox';
import Input from './Input';
import NameWindow from './NameWindow';

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
        maleNames:['will','john','bob','rick'],
        femaleNames:['Sarah','Jenny'],
    }
    this.addName = this.addName.bind(this);


    }

    addName(name){
        this.setState({
            names: this.state.names.push(name)
        })
    }

    render() {
        // console.log(this.state);
        return(
        //calculator
            <div className="masterWrapper">
                <div className="wrapper">
                    <h1>BMI Healthy Weight Calculator</h1>
                    <InnerBox/>
                </div>


                <div className="nameWrap">

                    <h1>Baby Name Inspiration</h1>

                    <Input 

                    />

                    <NameWindow 
                        names={this.state.names}
                    />

                    <AddNameBtn 
                        onClick={this.state.addName}
                    />


                </div>

            </div>

        )
    }



}
