import React from 'react';
import Titles from './components/Titles';
import Form from './components/Form';
import Weather from './components/Weather';
import './bootstrap.min.css';
import './styles.css';

const API_KEY = "016a6f0bc508419a9a9bbb66139efee0";

export default class App extends React.Component{
      constructor(props){
            super(props);
            this.state = {
                  temperature:undefined,
                  humidity:undefined,
                  city:undefined,
                  country:undefined,
                  description:undefined,
                  error:null,
                  forecastArray:[],
                  forecastD:null,
            }
            this.getWeather = this.getWeather.bind(this);
      }

      async getWeather(e){    
            e.preventDefault();
            const city = e.target.elements.city.value;
            const country = e.target.elements.country.value;
            const currWeatherApiCall = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}`);
            const forecastApiCall = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&appid=${API_KEY}`);
            const currWeatherData = await currWeatherApiCall.json();
            const forecastData = await forecastApiCall.json();
            console.log(forecastData);

            console.log(forecastData.list);

            const forecastArray = [];

            console.log(forecastArray);

            // forecastData.list.forEach(function(list){
            //       let date = list.dt_txt;
            //       let temp = list.main.temp;
            //       forecastArray.push(list);
            //       let weather = list.weather[0].main;
            //       console.log(date)
            //       console.log(temp)

            //       console.log(weather)
            //       const forecastD = {
            //             date:date,
            //             temp:temp,
            //             weather:weather,
            //       }
            //       console.log('--------')
            //       return forecastD;
            // })

            let date = forecastData.list.dt_txt;
            let temp = forecastData.list.main;
            let weather = forecastData.list.weather[0].main;
            const forecastD = {
                  date:date,
                  temp:temp,
                  weather:weather,
            }
            console.log(forecastD);




            this.setState({
                  temperature:currWeatherData.main.temp,
                  humidity:currWeatherData.main.humidity,
                  city:currWeatherData.name,
                  country:currWeatherData.sys.country,
                  description:currWeatherData.weather[0].description,
                  error:null,
                  foreCastArray:forecastArray,
                  forecastD:forecastD,
            })

      }

      render(){
            return(
                  <div className="container">
                        <Titles/>
                        <Form 
                              getWeather={this.getWeather}
                        />
                        <Weather 
                              humidity={this.state.humidity}
                              city={this.state.city}
                              country={this.state.country}
                              error={this.state.error}
                              description={this.state.description}
                              temperature={this.state.temperature}
                              forecastArray={this.state.forecastArray}

                        />  

                  </div>

            )
      }
}
