import React, { Component } from 'react';
import './index.css';
import AddNameBtn from './AddNameBtn';
import InnerBox from './InnerBox';
import Input from './Input';
import NameWindow from './NameWindow';

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
        names:['will','bob','john','bill','fred','rick','joe'],
        search:'',
    }
    this.filterNames = this.filterNames.bind(this);
    this.search = this.search.bind(this);

    }

    renderNames(name){
        const {search} = this.state;
        const names = this.state;
        filteredList = names.filter(function(name){
            return name.toLowerCase().indexOf(search.toLowerCase() )!== -1
        })
    }

    search(e){
        this.setState(
            {
               search: e.target.value
            }

        );


        // console.log(this.state.search);
    }



    render() {
        // console.log(this.state);
        return(
        //calculator
            <div className="masterWrapper">
                <div className="wrapper">
                    <h1>BMI Calculator</h1>
                    <InnerBox/>
                </div>


                <div className="nameWrap">

                    <h4>Name Inspiration</h4>

                    <div className="inputWrap">
                        <input placeholder="Add or search a name" ref="nameInput"  onChange={this.search.bind(this)}></input>
                    </div>
                    <div className="nameWindow" names={this.state.names}>
                        {
                           filteredNames.map(name => {
                               return this.renderNames(name)
                           })
                        }
                    </div>

                </div>

            </div>

        )
    }



}
