import React, { Component } from 'react';
import './App.css';
import ProductCategoryRow from './ProductsWindow.js';
import ProductRow from './ProductRow';

export default class ProductsWindow extends React.Component{

      render(){
            const products = this.props.products;

            const rows = [];
            let lastCategory = null;
            products.foreach(product => {
                  if(product.category !== lastCategory){
                      rows.push(
                              <ProductCategoryRow 
                                    category={product.category}
                              />
                        );  
                  }
                  rows.push(
                        <ProductRow
                              product={product}

                        />
                  )

            })

            return(
                  <table>
                        <thead>

                        </thead>
                        <tbody>

                        </tbody>
                  </table>
            )
      }
}
