import React, { Component } from 'react';

export default class Input extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            value:''
        };
        this.search = this.search.bind(this);
    }

    search(e){
        this.setState({
            value: e.target.value
        })
    }

    render(){
        return(
            <div className="inputWrap">
                <input placeholder="Add or search a name" ref="nameInput" value={this.state.value} onChange={this.search}></input>
            </div>
        )
    }
}
